import { useState } from "react";

export default function ticketBox({props}){
    const [paid, setPaid] = useState(false)
    const [qrcode, setQrcode] = useState(false)
    const qrarea = function(){
        return(
            <div className={styles.qrarea}>
                <button className={clsx(styles.button_1,styles.button_x)} >X</button>
                <img className={styles.qrpic} src="" alt="" />
                <button className={styles.button_1} >Đã thanh toán</button>
            </div>
        )
    }
    return (
        <div className={styles.ticketBox} id="ticket">
            <img src={props.thumbnail} />
            <div className={styles.name}>{props.name}</div>
            <div>Địa điểm: {props.location}</div>
            <div>Thời gian: {props.time}</div>
            <div>Giờ mở cửa: {props.opentime}</div>
            <div>
            {
                !paid && (<button className={styles.button_1} >Thanh Toán</button>
            
        )&& (<button className={styles.button_1} >Xóa Vé</button>)}
            {
                paid&& <button className={styles.button_1} >Xem vé</button>
            }
            </div>
</div>  
    )
}
