import React, { useState, useEffect } from 'react';
import axios from 'axios';

import Header from "../../components/Header/header"
import Footer from '../../components/Footer/footer';
import TicketBox from "../../components/TicketBox/ticketBox"
import TicketPopup from "../../components/TicketPopup/ticketPopup"
import styles from './ticketBuy.module.css'

export default function Watch() {
    var [exh, setExh] = useState([])
    var [exhspics, setExhspics] = useState([])
    var [loading, setLoading] = useState(false)
    useEffect(() => {
        setLoading(true)
        axios
            .get('http://localhost:5713/watch')
            .then((response) => {
                setExh(response.data.exhs)
                setLoading(false)
            })
            .catch((error) => {
                console.log(error);
            })
    }, [])

    useEffect(()=>{
        const fetchData= async()=>{
            try {
        setLoading(true)
            const response = await axios.get('http://localhost:5713/exhspics')
                setExhspics.response.exhspics
        } catch (error) {
                console.log(error)
            }
        }
        axios
            .get('http://localhost:5713/exhspics')
            .then((response)=>{
                setExhspics(response.data.exhspics)
                
            })
            .catch((error)=>{
                console.log(error)
            })
    },[])
    useEffect(() => {
        exh.map((e) => {
          let idartist = e.idartist;
          let pic = exhspics.find((e) => e.idartist === idartist);
          if (pic) {
            e.thumbnail = pic.picsrc;
            console.log(e.thumbnail);
          }
          let pics = exhspics.filter((e) => e.idartist === idartist);
          e.pics = pics;
        });
      }, [exh, exhspics]);
    var props1 = {
        name : "fdslak",
        location : "fklsadf",
        time : "flasdkf",
        thumbnail: "flksadf",
        opentime : "fklsdkf",
        pics: ["https://upload.wikimedia.org/wikipedia/commons/2/21/La_Vague_%28Gustave_Courbet_-_Mus%C3%A9e_des_beaux-arts_de_Lyon%29.jpg"]
    }
    return (
        <div className={styles.ticketbuy}>
            <Header />
            {/* <TicketBox1 props={props1}/> */}
            <div className={styles.tickets}>
            <TicketPopup props={props1} />

                {
                    loading ? (
                        <div>Loading....</div>
                    ) : (
                            exh.map((exh, index) => {
                               return <TicketBox props={exh} />
                            })
                    )
                }
            </div>
            <Footer/>


        </div>



    )
}
